//使用Uglify压缩的math.js
Math.isInt=function(a){return/^[\+\-]?[\d]+$/.test(a)||/^0[0-7]+$/.test(a)||/^[01]+$/.test(a)||/^[0-9a-f]+$/i.test(a)};Math.sign=function(a){return isNaN(a)?NaN:0<a?1:0>a?-1:0};Math.root=function(a,b){return isNaN(b)||isNaN(a)?NaN:Math.pow(a,1/b)};Math.t2a=function(a){return isNaN(a)?NaN:a/PI*180};Math.a2t=function(a){return isNaN(a)?NaN:a*PI/180};Math.fix=function(a,b){if(isNaN(b)||isNaN(a))return NaN;var c=Math.pow(10,b);return Math.round(a*c)/c};
Math.range=function(a,b){if(isNaN(a)||isNaN(b))return NaN;var c=Math.min(a,b),d=Math.max(a,b);return Math.floor(Math.random()*(d-c))+c};Math.HCF=function(a,b){if(isNaN(a)||isNaN(b))return NaN;var c=Math.max(a,b),d=Math.min(a,b);return c%d?Math.HCF(d,c%d):d};Math.LCM=function(a,b){return isNaN(a)||isNaN(b)?NaN:a*b/Math.HCF(a,b)};Math.module=function(){var a=arguments;if(!a.length)return 0;a[0]instanceof Array&&(a=a[0]);var b=0,c;for(c in a)b+=Math.pow(1*a[c],2);return Math.sqrt(b)};
Math.sigma=function(a,b,c){if(!Math.isInt(a)||!Math.isInt(b))return NaN;var d=Math.max(a,b);b=Math.min(a,b);a=0;for(c instanceof Function||(c=function(a){return a});b<=d;b++){var e=c(b);if(void 0===e||isNaN(e))return NaN;a+=1*c(b)}return a};Math.factorial=function(a){return/^[\+]?[\d]+$/.test(a)?1==a?1:Math.factorial(a-1)*a:NaN};Math.Fibonacci=function(a){return/^[\+]?[\d]+$/.test(a)?1==a||2==a?1:Math.Fibonacci(a-1)+Math.Fibonacci(a-2):NaN};